INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('Humorous Monkey Selfie in Tropical Jungle', 'A detailed, photorealistic prompt for generating a humorous, wide-angle travel selfie in a tropical forest. It uses strict facial identity locks and mandates a macaque monkey dominating the foreground, appearing to take the selfie with a surprised expression, while a woman laughs in the background, achieving a candid, ultra-wide smartphone aesthetic.', '{
  "generation_request": {
    "meta_data": {
      "tool": "NanoBanana Pro",
      "task_type": "photoreal_travel_selfie_wide_angle_comedy",
      "version": "v1.0_MONKEY_SELFIE_JUNGLE_PATH_FACELOCK_SAFE_EN",
      "priority": "highest",
      "language": "en"
    },

    "references": {
      "reference_image_style": {
        "source": "UPLOAD_REFERENCE_IMAGE (REQUIRED)",
        "purpose": "STYLE_COMPOSITION_POSE_LIGHTING_ANCHOR",
        "strict_lock": true,
        "preserve_pose": true,
        "preserve_composition": true,
        "preserve_lighting": true,
        "preserve_vibe": true,
        "preserve_palette": true
      },
      "reference_image_face": {
        "source": "UPLOAD_REFERENCE_IMAGE (OPTIONAL)",
        "purpose": "FACE_IDENTITY_LOCK",
        "strict_lock": true,
        "face_similarity_priority": "MAX",
        "no_identity_blending": true,
        "no_beautify": true,
        "no_age_shift": true,
        "preserve_skin_texture": true,
        "preserve_facial_proportions": true,
        "preserve_moles_freckles_scars": true,
        "preserve_eye_shape": true,
        "preserve_nose_shape": true,
        "preserve_lip_shape": true,
        "preserve_jawline": true
      }
    },

    "global_constraints": {
      "rating": "PG-13",
      "no_nudity": true,
      "no_explicit_sexual_content": true,
      "no_logos": true,
      "no_watermark": true,
      "no_readable_text": true
    },

    "output_settings": {
      "aspect_ratio": "9:16",
      "orientation": "portrait",
      "resolution_target": "ultra_high_res",
      "render_style": "cinematic_photoreal_candid",
      "sharpness": "crisp_but_natural",
      "film_grain": "subtle",
      "color_grade": "natural_daylight_warm_green",
      "dynamic_range": "natural_not_hdr",
      "skin_rendering": "real_texture_no_retouch"
    },

    "creative_prompt": {
      "scene_summary": "Create a photoreal travel selfie in a lush tropical forest. Setting: a narrow outdoor walkway/path with wet ground, dense green foliage, tall trees, and a humid jungle atmosphere.\n\nForeground: a macaque monkey extremely close to the camera lens, dominating the frame with a humorous wide-angle distortion. The monkey’s arm reaches forward as if it is taking the selfie. The monkey mouth is open in a funny surprised expression. Realistic fur detail, natural anatomy, no deformation.\n\nBackground: one adult woman behind the monkey, laughing with an open-mouth smile, both hands raised doing a double peace sign. Natural candid joy. Hair in a casual updo with soft bangs framing the face. Light summer outfit ({argument name="outfit color" default="white"} dress or top), minimal jewelry. One wrist has a simple yellow wristband (no readable text).\n\nCinematography: smartphone ultra-wide selfie look (14mm equivalent), close perspective, mild lens distortion, natural daylight, soft shadows, realistic skin texture, no heavy retouch, no beauty filter. Keep it fun and spontaneous, high realis"', 'Nano Banana Pro', '{"Nano Banana", "Imported", "CSV"}', '{"https://pbs.twimg.com/media/G_g0usDXsAAezQ8.jpg"}', '{"sourceLink": "https://x.com/astronomerozge1/status/2015419031931486385", "sourcePublishedAt": "2026-01-25T13:38:36.000Z", "author": "{\"name\":\"\u00d6zge D\u00f6ner\",\"link\":\"https://x.com/astronomerozge1\"}", "imported_from": "csv_batch_20260128"}')
