INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('Cinematic Artisan Portrait Prompt for Model Making Workshop', 'This detailed prompt is designed to generate a cinematic, National Geographic-style portrait of a craftsman working on a miniature model of a famous structure, like the Death Star. It specifies the aesthetic, materials, lighting, and composition to create a highly atmospheric image of a cluttered workshop.', 'Do this for {argument name="structure name" default="Death Star"}: <instruction>
