INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('Mythological Forest Portrait with Renaissance Light', 'A highly detailed image generation prompt for a cinematic art portrait in the style of Renaissance Light (Caravaggio). The subject, a woman, is kneeling in an ancient forest grove, covered minimally by large tropical leaves, holding a polished deep red apple. The focus is on extreme shallow depth of field and hyper-detailed textures under mystical early morning fog.', '{
