INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('Character Relationship Chart Infographic', 'A detailed prompt template for Nano Banana Pro to generate a character relationship chart (相関図) infographic. It requires uploading character images and defining names, roles, and specific relationships, which are then visualized using color-coded arrows (red for positive, blue for negative) in a hand-drawn BD anime illustration style.', '# 命令書
