INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('Dynamic Black and White Street Fashion Prompt', 'A detailed JSON prompt for Nano Banana Pro to generate a hyper-realistic black and white dynamic street fashion photograph. The subject is a stylish woman walking briskly across a busy urban intersection, wearing an oversized blazer and loose trousers. The prompt specifies a paparazzi aesthetic, frontal tracking shot perspective, high contrast rim lighting, and vintage film stock emulation for post-processing.', '{
