INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('Image Restoration Prompt for Nano Banana', 'A prompt designed for Nano Banana to completely repair corrupted line art and coloring in an image that has been degraded through multiple edits. It instructs the AI to restore sharp, smooth lines, accurate and clean coloring, remove noise from degradation, and output a high-quality, vivid illustration.', '"この画像の崩れてしまった線画と塗りを、最初の綺麗な状態に完全に修復してください。線はブレや歪みがひとつもなくシャープで滑らかに、塗りはムラなく正確でクリーンになるように描き直してください。画像の劣化によるノイズをすべて取り除き、高画質で鮮明なイラストとして仕上げてください。"', 'Nano Banana Pro', '{"Nano Banana", "Imported", "CSV"}', '{"https://pbs.twimg.com/media/G8gY4z6akAA8uHn.jpg","https://pbs.twimg.com/media/G8gY67LaIAARaac.jpg"}', '{"sourceLink": "https://x.com/AbaneChan/status/2001877487433974268", "sourcePublishedAt": "2025-12-19T04:49:20.000Z", "author": "{\"name\":\"\u5104\u4e07\u682a\u59eb\u2606\u3042\u3070\u306d\u3061\u3083\u3093\uff08\uff12\uff10\u6b73\uff09\",\"link\":\"https://x.com/AbaneChan\"}", "imported_from": "csv_batch_20260128"}')
