INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('8K Ultra-Realistic Kashmir Winter Scene Prompt', 'A detailed text prompt for Nano Banana Pro generating an 8K ultra-realistic, cinematic winter scene in Kashmir during Chillai Kalan (the harshest period of winter). It emphasizes heavy snowfall, traditional houses, frost details, and Kashmiri people in pherans holding glowing kangri fire pots, using a cold blue-white palette contrasted with warm orange light.', 'A cinematic, ultra-realistic winter scene in {argument name="location" default="Kashmir"} during {argument name="period" default="Chillai Kalan"}. Heavy snowfall blankets traditional {argument name="building type" default="wooden houses"}, rooftops covered in thick ice and snow. Frost clings to windows, icicles hanging from eaves. Kashmiri people dressed in traditional woolen {argument name="clothing" default="pherans"} stand outdoors and near doorways, holding glowing {argument name="prop" default="kangri fire pots"} close to keep warm. Visible breath in the freezing air, snowflakes falling steadily. Moody overcast sky, cold {argument name="color palette" default="blue-white color palette"} with warm {argument name="light color" default="orange glow"} from the kangri fires. Hyper-detailed textures of snow, ice, fabric, and skin. Dramatic cinematic lighting, shallow depth of field, realistic weather effects, winter storm atmosphere. 8K resolution, ultra-sharp focus, photorealistic, documentary-style promotional realism.', 'Nano Banana Pro', '{"Nano Banana", "Imported", "CSV"}', '{"https://pbs.twimg.com/media/G8i0IWva8AAJENL.jpg","https://pbs.twimg.com/media/G8i0IYQbsAEC6JR.jpg","https://pbs.twimg.com/media/G8i0IZ3bYAA0OAa.jpg","https://pbs.twimg.com/media/G8i0IcPasAAS35w.jpg"}', '{"sourceLink": "https://x.com/Milo_Bahi_02/status/2002048109808079200", "sourcePublishedAt": "2025-12-19T16:07:20.000Z", "author": "{\"name\":\"Milo\",\"link\":\"https://x.com/Milo_Bahi_02\"}", "imported_from": "csv_batch_20260128"}')
