INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('Three Identity-Locked Photorealistic Portraits', 'A collection of three distinct prompts, all focused on generating highly photorealistic portraits while strictly preserving the facial identity of an uploaded reference image. They cover different styles: an extreme close-up beauty shot, a cinematic travel portrait in a snowy alpine train, and a retro vintage 90s look with a red dress and flowers.', 'Prompt 1:
