INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('3D Isometric Luxury Pop-Up Kiosk Render', 'A prompt designed for Nano Banana Pro to generate a 3D isometric render of a luxury pop-up shop kiosk. The kiosk architecture is shaped exactly like a brand''s logo, constructed from polished materials with neon LED strips tracing the silhouette. It specifies high-end details, integrated lighting, and an ultra-photorealistic Octane render quality for a luxury design aesthetic.', '3D isometric render of a luxury pop-up shop kiosk shaped exactly like the {argument name="brand name and logo shape" default="[BRAND_NAME_AND_LOGO_SHAPE]"}. The architecture is a thick 3D extrusion of the logo symbol, constructed from high-end polished architectural materials in glossy {argument name="brand color" default="[BRAND_COLOR]"} with subtle brushed metal accents. Thin, glowing neon LED strips trace the entire outer silhouette and edges of the logo shape. Large, seamless museum-grade glass windows showcase a meticulously detailed interior. Inside, bespoke shelves present {argument name="product type" default="[PRODUCT_TYPE]"} with integrated micro-LED lighting. Dramatic high-end studio lighting creates soft shadows and highlights, emphasizing textures and form. Set against a clean, neutral gallery background. Ultra-photorealistic, 8k resolution, highly detailed, octane render quality, luxury design aesthetic.', 'Nano Banana Pro', '{"Nano Banana", "Imported", "CSV"}', '{"https://pbs.twimg.com/media/G75mi2KXsAIMMXW.jpg","https://pbs.twimg.com/media/G75mizIXIAA5xH1.jpg","https://pbs.twimg.com/media/G75mi4oWYAAwLbh.jpg","https://pbs.twimg.com/media/G75mkYOXsAADhpz.jpg"}', '{"sourceLink": "https://x.com/Arminn_Ai/status/1999148326948647111", "sourcePublishedAt": "2025-12-11T16:04:38.000Z", "author": "{\"name\":\"\u039bRMIN | AI\",\"link\":\"https://x.com/Arminn_Ai\"}", "imported_from": "csv_batch_20260128"}')
