INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('1x3 Grid Phone Motion Edit JSON Prompt', 'A highly detailed JSON prompt structure for Nano Banana Pro designed to generate a 1x3 grid of images. The primary function is ''phone motion edit,'' where a person''s photo is uploaded along with content for the phone screen, ensuring identity consistency and specific poses like looking at the phone, holding it forward, and a jump cut.', '{
  "layout": {
    "type": "grid_1x3"
  },

  "global": {
    "edit_type": "phone_motion_edit",

    "source": {
      "mode": "EDIT",
      "reference_images": {
        "first": "base_photo_person_and_environment",
        "second": "screen_content_for_phone"
      },
      "preserve_from_first": {
        "same_person_or_group": true,
        "same_faces": true,
        "same_hairstyles": true,
        "same_outfits": true,
        "same_environment_style": true
      }
    },

    "identity": {
      "keep_identity_consistent": true,
      "expression": "bright_natural_smile"
    },

    "phone": {
      "allowed": true,
      "holding_styles": [
        "one_handed",
        "two_handed",
        "near_chest",
        "near_hip",
        "tilted",
        "sideways",
        "partially_toward_lens"
      ],
      "rules": {
        "each_person_may_hold_one_phone": true,
        "screen_should_be_naturally_visible": true,
        "do_not_force_phone_directly_toward_camera": true
      }
    },

    "screen_replacement": {
      "use_second_reference_image_as_content": true,
      "fit_without_distortion": true,
      "no_ui": true
    },

    "environment": {
      "preserve_environment_style_from_reference": true,
      "lighting_consistent_with_reference": true
    },

    "constraints": {
      "no_new_characters": true,
      "no_costume_change": true,
      "no_change_to_reference_location_type": true
    }
  },

  "panels": [
    {
      "id": "panel_2",
      "role": "phone_view_soft",
      "edit": true,
      "pose": {
        "pose_can_change": true,
        "style_tags": ["looking_at_phone", "face_close", "natural_pose"],
        "focus_on_expressive_hands": true
      }
    },

    {
      "id": "panel_3",
      "role": "phone_forward",
      "edit": true,
      "pose": {
        "pose_can_change": true,
        "style_tags": ["hand_forward", "phone_front_soft", "arm_extended"],
        "focus_on_expressive_hands": true
      },
      "camera_effect": {
        "perspective": "wide_angle",
        "near_objects_appear_large": true
      }
    },

    {
      "id": "panel_4",
      "role": "jump_cut",
      "edit": true,
      "use_preset": "jump_frame"
    }
  ],

  "pose_presets": {
    "jump_frame": {
      "pose": {
        "pose_can_change": true,
        "style_tags": ["jump", "lively", "mid_air"],
        "focus_on_expressive_hands": true
      },
      "camera_effect": {
        "perspective": "wide_angle",
        "allow_view_from_below": true
      }
    }
  }
}', 'Nano Banana Pro', '{"Nano Banana", "Imported", "CSV"}', '{"https://pbs.twimg.com/media/G7o9xAGbAAApmgM.jpg"}', '{"sourceLink": "https://x.com/SDT_side/status/1997977301703954705", "sourcePublishedAt": "2025-12-08T10:31:24.000Z", "author": "{\"name\":\"SDT\ud83c\udf3f\",\"link\":\"https://x.com/SDT_side\"}", "imported_from": "csv_batch_20260128"}')
