INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('Hyper-Realistic iPhone Flash Selfie Prompt', 'An extremely detailed, structured JSON prompt designed to generate a hyper-realistic, edgy, late-night iPhone flash selfie of an alt-girl. It meticulously defines the style, age, subject''s appearance (hair, face, expression, outfit), props, setting, lighting, technical camera details, and includes a comprehensive list of elements to avoid.', '{
