INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('3D chibi miniature brand concept store prompt', 'A reusable prompt to generate 3D chibi-style miniature concept stores for any brand, focusing on their signature product, colors, and cozy interior with tiny figures.', '{argument name="ブランド名" default="スターバックス"}の3Dちびスタイルミニチュアコンセプトストアで、ブランドの最も象徴的な商品やパッケージ（例: 巨大な{argument name="ブランドのコア製品" default="チキンバケット/ハンバーガー/ドーナツ/ローストダック"}）にインスパイアされた外観で創造的にデザインされています。店舗は2階建てで、大きなガラス窓があり、居心地の良い細部まで装飾された内部を明確に展示しています: {argument name="ブランドのプライマリカラー" default="ブランドのプライマリカラー"}をテーマにした装飾、暖かい照明、そしてブランドに合わせた衣装を着た忙しいスタッフ。可愛らしい小さなフィギュアが通りを歩いたり座ったりし、ベンチ、街灯、鉢植えに囲まれ、魅力的な都市のシーンを作り出しています。Cinema 4Dを使用してミニチュアシティスケープスタイルでレンダリングされ、ブラインドボックス玩具の美学を持ち、細部が豊富でリアリズムがあり、リラックスした午後の雰囲気を呼び起こす柔らかい照明に包まれています。登場するミニキャラたちは添付のキャラクターシートを参照に登場させてください。--ar 2:3', 'Nano Banana Pro', '{"Nano Banana", "Imported", "CSV"}', '{"https://pbs.twimg.com/media/G7ImDCwbgAQkqWu.jpg","https://pbs.twimg.com/media/G7ImDCybQAADObo.jpg","https://pbs.twimg.com/media/G7ImDCybgAo73uF.jpg","https://pbs.twimg.com/media/G7ImDCyaMAA7H8f.jpg"}', '{"sourceLink": "https://x.com/tetumemo/status/1995699440695607443", "sourcePublishedAt": "2025-12-02T03:39:59.000Z", "author": "{\"name\":\"\u30c6\u30c4\u30e1\u30e2\uff5cAI\u56f3\u89e3\u00d7\u691c\u8a3c\uff5cNewsletter\",\"link\":\"https://x.com/tetumemo\"}", "imported_from": "csv_batch_20260128"}')
