INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('Nano Banana Pro torn paper style-switching edit prompt', 'A detailed Nano Banana Pro image-editing prompt that adds horizontal torn-paper bands whose interiors can switch between line art, sumi-e, figure render, colored pencil, watercolor, or pencil drawing styles while preserving the original character and scene.', 'task: "edit-image: add widened torn-paper layered effect"

