INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('Anthropic-style warm academic PPT workflow prompt', 'A workflow-style prompt for Nano Banana Pro that first outlines a PPT from an article, then generates each slide in a warm, academic Anthropic/Claude-inspired design style.', '帮我根据下面这个文章做一套专业的的中文PPT。
