INSERT INTO cinematic_prompts (title, description, prompt_text, category, tags, image_urls, metadata)
VALUES ('Energy field barrier VFX and pose adjustment YAML prompt', 'A detailed YAML Nano Banana Pro prompt that changes a character’s pose to extend both arms forward and adds concentric octagonal energy-field barriers in front of the hands, while preserving all original character and background details.', 'task: "pose-and-barrier-vfx"

reference_image:
  keep_original: true

subject:
  keep_character_design: true

rules:
  - do not change the character’s face, expression, hairstyle, anatomy, or proportions
  - do not change outfit, colors, lineart, or art style
  - do not change background, composition, shading, or lighting
  - all modifications occur only through the defined steps

steps:

  - step: "pose-adjustment"
    action:
      - extend both arms forward toward the viewer
      - rotate both palms to face directly toward the camera
      - fingers naturally spread
    constraints:
      forbidden:
        - altering facial expression
        - modifying outfit
        - adding props or new items
        - introducing glow or VFX

