    vfx:
      type: "concentric-octagonal-barrier"
      placement: "in front of both palms"

      geometry:
        shape: "regular octagon"
        structure: "multiple concentric octagonal surfaces"
        alignment:
          - all surfaces share the same center
          - all surfaces are parallel to the camera plane
          - no curvature or spherical distortion
        forbidden:
          - outline-only shapes
          - hollow frames
          - hexagonal shapes
          - domed or warped surfaces

      surface:
        fill: "center-to-edge radial gradient"
        colors:
          center: "white-yellow"
          mid: "orange-gold"
          edge: "transparent orange"
        glow:
          - strong center luminosity
          - soft bloom
          - refractive shimmer
        opacity: "semi-transparent"

      layering:
        count: {argument name="number of barrier layers" default="8"}
        direction: "stacked forward"

  - step: "text-overlay"
    text:
      content: "Prompt by てんねん@ munou_ac"
      position: "bottom-left"
      font_color: "white"
      font_size: "small"
      opacity: "0.9"

composition:
  camera:
    angle: "frontal"
    distance: "medium shot"
    focus: "hands and barrier surface"
  lighting:
    keep_original: true
  placement:
    barrier_position: "centered between both palms"
    size: "large frontal barrier"', 'Nano Banana Pro', '{"Nano Banana", "Imported", "CSV"}', '{"https://pbs.twimg.com/media/G64gFJzbkAQpKL2.jpg","https://pbs.twimg.com/media/G64gKH-bkAQsjS9.jpg"}', '{"sourceLink": "https://x.com/munou_ac/status/1994567138384994796", "sourcePublishedAt": "2025-11-29T00:40:37.000Z", "author": "{\"name\":\"\u3066\u3093\u306d\u3093\",\"link\":\"https://x.com/munou_ac\"}", "imported_from": "csv_batch_20260128"}')
